INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Écraser en miettes d’1 paquet de petit beurre et les mélanger avec 100g de beurre doux.", "1");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Disposer la préparation au fond d'un moule à fond amovible. Bien tasser.", "1");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Préparer le caramel beurre salé:", "1");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Faire chauffer 5 cuillères à soupe de sucre dans une casserole, dès que le caramel colore, y ajouter les 50g de beurre salé et les 20cl de crème liquide préalablement chauffée dans une casserole.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Disposer le caramel beurre salé refroidi sur le gâteau.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Couper 3 bananes en rondelles et les disposer sur le caramel.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Battre 2 blancs d’œufs en neige ferme.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Mélanger 250g de mascarpone avec '1' jaune d'œuf, ajouter 3 cuillères à soupe de sucre.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Ajouter délicatement à cette préparation les blancs en neige.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("10. Mettre le tout dans une poche à douille et disposer sur les bananes en formant des petits dômes.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("11. Réserver au frais.", '1');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("12. Au moment de servir saupoudrer de cacao amer pour la décoration.", '1'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("13. A faire 4 à 6 heures à l'avance.", '1');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Faire bouillir 90cl de lait avec 1 gousse de vanille fendue dans la longueur et en ayant préalablement gratté la gousse.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Laisser infuser la préparation 5 minutes, puis retirer la gousse.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Séparer les jaunes de 6 œufs et les battre avec 80 g de sucre.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Ajouter le lait chaud puis remettre sur feu doux pour faire épaissir le mélange. Agiter sans arrêt avec une cuillère en bois et sans faire bouillir la crème.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  La crème est prise lorsque la mousse blanche en surface disparaît.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Mettre au réfrigérateur une fois la préparation refroidie.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Dans une grande casserole, mettre 2 l d'eau à bouillir.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Monter les blancs en neige avec une pincée de sel et ajouter doucement 30 g de sucre.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Former des boules de blancs d'œuf avec deux cuillères à soupe et les faire cuire toutes les faces dans de l'eau bouillante.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("10. Compter 1 min et égoutter sur papier absorbant.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("11. Dorer 45g d’amandes à la poêle.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("12. Faire un caramel blond avec 90g de sucre en morceaux, un filet de citron et 2 cuillères à soupe d'eau.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("13. Mettre la crème dans un plat creux. Y déposer les blancs puis décorer avec des filets de caramel et un peu d'amandes.", '2'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Séparer les blancs des jaunes de 5 oeufs.", '3');  
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Mélanger les jaunes avec 150g de sucre roux et 1,5 sachet de sucre vanillé.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Ajouter 375g de mascarpone au fouet.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Monter les blancs en neige et les incorporer délicatement à la spatule au mélange précédent. Réserver.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Mouiller 36 biscuits dans le café (environs 75cl de café noir non sucré) rapidement avant d'en tapisser le fond du plat.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Recouvrir d'une couche de crème au mascarpone puis répéter l'opération en alternant couche de biscuits et couche de crème en terminant par cette dernière.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Saupoudrer de cacao.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Mettre au réfrigérateur 4 heures minimum puis déguster frais.", '3'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Préchauffer le four à 180°C (thermostat 6).", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Faire fondre 150g de chocolat et 75g de beurre au bain-marie à feu doux, ou au micro-ondes sur le programme 'décongélation'.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Pendant ce temps, séparer les jaunes des blancs de 4 œufs.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Monter les blancs en neige ferme. Réserver.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Quand le mélange chocolat-beurre est bien fondu, ajouter les jaunes d’œufs et fouetter.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Incorporer 75g de sucre et 3 cuillères à soupe de farine, puis ajouter les blancs d’œufs sans les casser.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Beurrer et fariner un moule à manquer et y verser la pâte à gâteau.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Enfourner pendant 20 minutes.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Quand le gâteau est cuit, le laisser refroidir avant de le démouler.", '4'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Mettre 1 pâte feuilletée dans un plat à tarte, la piquer avec une fourchette, garnir avec 5 pommes coupées en morceaux.", '5'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Mélanger 90g sucre, 2 œufs, 60g de beurre fondu et verser sur la tarte.", '5'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Enfournez, thermostat 6-7 ( 200°) pendant 35 minutes environ.", '5'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Faire fondre 225g de beurre au bain marie.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Mélanger 450g de biscuits (types Lu) écrasés et le beurre fondu.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Tasser cette préparation dans le fond d'un petit moule et mettre au réfrigérateur.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Mélanger 9 œufs à 15 petits suisses un par un.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Battre et ajouter 1,5 cuillère à soupe de jus de citron et 150g de sucre.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Attendre 2 heures.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Cuire à 180°C (thermostat 6) durant 1 heure.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Ajouter du coulis de fruits rouges sur le tout et démouler.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Mettre au à nouveau au réfrigérateur.", '6'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Mélanger 100g de sucre avec 100g de farine. Y ajouter 3 œufs. Mélanger le tout puis y incorporer un paquet de levure.", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Verser la préparation dans un moule à baba beurré au préalable.", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Mettre à four chaud (déjà chauffé depuis 15 minutes environ) à 200°C durant 15 mn (la cuisson est très importante normalement il ne faut pas dépasser ces 15 minutes !!!).", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Piquer avec un couteau pour voir si la pâte est bien cuite.", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Pendant la cuisson du baba : verser dans une casserole 70g de sucre avec 500ml d’eau et dès les premiers bouillons y verser 250ml de rhum et éteindre le feu.", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Lorsque le baba est sorti du four, il ne faut surtout pas le démouler, y verser (encore chaud) le sirop tout autour. Il faut bien l'imbiber partout.", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Lorsque le baba est froid le mettre au frigo au moins 10 heures.", '7'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Déroulez la pâte brisée avec sa feuille de cuisson dans un moule à tarte. Faites cuire à blanc 20 minutes à 180°C (thermostat 6).", '8');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Pendant ce temps, travaillez 1 œuf entier et 2 jaunes et 80g de sucre. Incorporez 30g de farine et 500ml de lait chaud. Faites épaissir le mélange dans une casserole à feux doux en tournant avec une cuillère en bois.", '8');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Retirez du feu dès que la crème est prise. Ajoutez 1 sachet de sucre vanillé.", '8');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Sortez le fond de tarte du four. Laissez refroidir. Versez-y la crème.", '8');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Disposez 300g de framboises en cercle sur la crème, pour faire une belle présentation.", '8');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Nappez de gelée de framboises liquéfiée avec 2 cuillerées d'eau.", '8');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Servez froid...", 8);
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Pour la pâte à choux:", 9);
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Préchauffer le four à 210°C (Thermostat 7).", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Mélanger 1 pincée de sel, 1 cuillère à soupe de sucre, 75g de beurre et 25cl d’eau dans une casserole, et faire bouillir.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Intégrer 150g farine, et remuer jusqu'à l'obtention d'une pâte compacte. La travailler jusqu'à ce qu'elle soit suffisamment ferme", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Intégrer 4 œufs, un à un en veillant à bien mélanger entre chaque œuf.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Travailler la pâte afin de la rendre ferme.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Sur un plaque allant au four préalablement huilée, répartir à l'aide de la poche à douille une dizaine de boudins de pâte de 15 cm de long environ.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Badigeonner avec 1 jaune d'œuf pour que la pâte soit dorée à la cuisson .", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Faire cuire 25 min à four chaud et laisser reposer 10 min, four éteint, pour éviter que les choux ou les éclairs ne dégonflent.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("10. Pour la crème:", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("11. Faire fondre 60 g de chocolat cassé en morceaux dans 30cl de lait, à feu doux.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("12. Dans un bol, fouetter 1 œuf, 2 jaunes d’œufs et 3 cuillères à soupe de sucre jusqu'à ce que le mélange soit mousseux.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("13. Ajouter 2 cuillères à soupe de farine et verser dans le lait chocolaté.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("14. Faire épaissir sans cesser de remuer.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("15. Hors du feu, intégrer 20 g de beurre. Laisser refroidir.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("16. Garnir de cette crème les éclairs coupés en 2 dans le sens de la longueur et faire fondre au bain-marie 150g de chocolat et 30g beurre.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("17. Napper le dessus des éclairs. Laisser durcir le glaçage avant de déguster.", '9');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Pour la pâte sablée : mettre le four à préchauffer à 180°C (thermostat 6).", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Fouetter 2  jaunes d'œuf avec 70 g de sucre et 2 cuillères d'eau pour faire mousser.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Mélanger au doigt 250g de farine et 125g de beurre coupé en petits cubes pour obtenir une consistance sableuse et afin que tout le beurre soit absorbé (il faut faire vite pour que le mélange ne ramollisse pas trop).", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Verser au milieu de ce 'sable' le mélange liquide.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Former une boule avec les  de la main.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Foncer un moule de 25 cm de diamètre avec la pâte sablée puis la recouvrir de papier sulfurisé et de haricots secs.", '10'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Cuire à blanc 20 à 25 minutes (NB après baisser le four à 120°C/150°C environ pour la meringue).", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Pour la crème au citron : laver 4 citrons de taille moyen et en râper deux afin de récupérer le zeste.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Mettre les zestes très fins dans une casserole avec le jus des citrons, 150g de sucre et 1 cuillère à soupe de Maïzena.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("10. Remuer et commencer à faire chauffer à feux doux.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("11. Battre 3 œufs dans un récipient, incorporer tout en remuant le jus de citron, le sucre, la Maïzena et les zestes.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("12. Mettre à feu vif et continuer à remuer à l'aide d'un fouet. Le mélange va commencer à s'épaissir. Veiller à toujours remuer lorsque les œufs sont ajoutés car la crème de citron pourrait brûler.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("13. Ôter du feu et verser l'appareil sur le fond de tarte cuit.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("14. Laisser refroidir.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("15. Terminons par la meringue : monter 2 blancs en neige avec une pincée de sel.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("16. Quand ils commencent à être fermes, ajouter 100g de sucre glace puis ½ cuillère à café de levure. Fouetter jusqu'à ce que la neige soit ferme.", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("17. Recouvrir la crème au citron de meringue puis enfourner la tarte à 120°C/150°C jusqu’à ce que la meringue dore (environ 10 minutes).", '10');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Faites d’abord la crème. Portez 25cl de lait à ébullition. Fouettez 3 jaunes avec 60g de sucre jusqu’à ce qu’ils blanchissent, incorporez 20g de Maïzena. Versez dessus le lait bouillant en filet en fouettant. Faites épaissir sur feu moyen en remuant jusqu’à l’ébullition. Laissez refroidir.", '11');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Travaillez 125g de beurre en pommade. Incorporez la crème pâtissière et 115g de praliné, en fouettant afin de bien aérer la masse.", '11');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Allumez le four à 180°C . Couvrez la plaque de papier sulfurisé. Portez 12,5cl d’eau et de le lait à ébullition avec 55g de beurre, 10g de sucre et une pincée de sel. Ajoutez 50g de farine et 20g de Maïzena d’un coup. Remuez vivement jusqu’à ce que la pâte se détache des parois. Eteignez le feu, incorporez 2 oeufs un à un en fouettant avec vigueur. Cessez de fouetter quand la pâte est lisse.", '11');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Faites un cercle de pâte de 20 cm sur la plaque avec une poche à douille. Formez un second cercle dessus puis un troisième. Décorez d’amandes.", '11');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Faites dorer 30 mn au four. Laissez refroidir au four éteint, porte entrouverte. Tranchez la couronne dans l’épaisseur.",  '11');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Garnissez le bas de crème, remettez le couvercle et poudrez de sucre glace.", '11');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Préchauffer votre four à thermostat 8.", "12");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Éplucher, épépiner et couper 1kg de pommes en gros morceaux. Les faire réduire à feu fort 2 mn avec une cuillerée à soupe d'eau puis maintenir la cuisson à feu doux pendant 7 à 8 mn.", "12");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Écraser les derniers gros morceaux de pommes à la fourchette. Mélanger les pommes avec 125 g de sucre et 1/2 cuillère à café de cannelle. Verser ce mélange dans un plat à gratin beurré.", "12");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Travailler énergiquement 75g de beurre. Incorporer 125g de sucre et 1 cuillère à café rase de cannelle en poudre, ainsi que 150 g de farine, puis travailler le tout avec les doigts afin d'obtenir un sable grossier.", "12");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Répartir sur la compote et cuire au four 25 mn (grille centrale).", "12");
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Préchauffez Th.6 (180°C).", '13');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Dans une casserole, faites chauffer 125 ml d'eau, 40 g de beurre, 10 g de sucre et 1 pincée de sel. Versez 80 g de farine et 10g de cacao. Faites déssécher à feu doux environ 1 minute.", '13');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Hors du feu, ajoutez 2 oeufs un par un. Sur une plaque de four avec un papier sulfurisé beurré, faites des choux soit avec une poche à douille soit avec une cuillerée à café.", '13');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Faites cuire environ 30 minutes. Faites refroidir.", '13');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Dans un saladier, cassez 200g de chocolat en morceaux. Faites bouillir 200ml de crème liquide et versez-la sur le chocolat. Couvrez et attendez environ 2 minutes.", '13');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Coupez le haut des choux, mettez de la glace vanille dans le choux, puis remettez le chapeau. Couvrez de sauce au chocolat.", '13');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Préchauffez le four à 200 °C.", '14');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Faites bouillir 500 ml d'eau et infusez-y 1 sachet de thé Earl Grey. Ôtez le sachet, versez le thé sur 12 pruneaux et laissez mariner 10 min.", '14');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Égouttez les pruneaux et disposez-les dans un moule à gratin beurré.", '14');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Mélangez 80g de sucre, 60g de farine et 1 pincée de sel dans un saladier. Ajoutez 3 œufs un à un ainsi que l’arôme vanille et mélangez pour obtenir une crème bien lisse. Incorporez 1/3 de litre de lait et mélangez à nouveau.", '14');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Versez la pâte sur les pruneaux et enfournez environ 50 min.", '14');
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("1.  Préchauffer le four à 180°C (thermostat 6).", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("2.  Couper 100g de fraises en petits dés.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("3.  Faire fondre 100g de beurre.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("4.  Dans une jatte, battre au fouet 2 œufs et 125g de sucre jusqu'à ce que le mélange devienne mousseux.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("5.  Ajouter 4cl de lait, 4 cl d'arôme de vanille liquide puis le beurre fondu. Sans cesser de remuer, incorporer 125g de farine et 1 cuillère à café de levure chimique en 4 fois afin d'obtenir un mélange homogène.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("6.  Délicatement, ajouter les fraises à la préparation.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("7.  Répartir la pâte dans des moules à cupcakes et mettre au four 20 min.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("8.  Pour une cuisson optimale, toucher du bout des doigts la surface bombée elle doit reprendre sa forme initiale ensuite. Le 'dôme' doit être doré mais pas craquelé.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("9.  Laisser refroidir.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("10. Dans un bol, fouetter 150g de mascarpone, 40g de sucre glace et 2cl d’arome vanille liquide. Terminer par l'ajout de crème fraîche jusqu'à ce que le mélange devienne onctueux et facile à travailler.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("11. Remplir une poche à douille avec cette préparation puis décorer vos cupcakes totalement refroidis.", '15'); 
INSERT INTO `step` (`description`, `receipe_id`) VALUES ("12. Décorer de perles et de copeaux de chocolat avant de laisser le glaçage prendre de la consistance au réfrigérateur.", '15'); 
